const sectionBg = "linear-gradient(90deg, #e0e7ff 0%, #f7f8fa 100%)";
const accent = "#7f53ac";
const accent2 = "#43cea2";
const errorColor = "#e53e3e";

function App() {
  const [script, setScript] = useState("");
  const [jobId, setJobId] = useState("");
  const [status, setStatus] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  const [error, setError] = useState("");
  const [progress, setProgress] = useState(0);
  const [style, setStyle] = useState("");
  const [voice, setVoice] = useState("");
  const [styles, setStyles] = useState([]);
  const [voices, setVoices] = useState([]);
  const [subtitleFile, setSubtitleFile] = useState(null);
  const [coverImage, setCoverImage] = useState(null);
  const [overlays, setOverlays] = useState([
    { image: null, start: "", end: "", position: "" }
  ]);
  const [formError, setFormError] = useState("");
  const intervalRef = useRef(null);
  const [styleLoadError, setStyleLoadError] = useState(false);
  const [voiceLoadError, setVoiceLoadError] = useState(false);

  useEffect(() => {
    getImageStyles().then(setStyles).catch(() => {
      setStyleLoadError(true);
      setStyles([]);
    });
    getTTSVoices().then(setVoices).catch(() => {
      setVoiceLoadError(true);
      setVoices([]);
    });
  }, []);

  // 校验逻辑
  const validate = () => {
    if (!script.trim()) return "脚本不能为空";
    if (!style || styleLoadError) return "请选择图片风格（如下拉不可用请检查后端服务）";
    if (!voice || voiceLoadError) return "请选择TTS声音（如下拉不可用请检查后端服务）";
    for (let i = 0; i < overlays.length; i++) {
      const o = overlays[i];
      if (o.image || o.start || o.end || o.position) {
        if (!o.image) return `第${i+1}个动态元素图片未上传`;
        if (!o.start || isNaN(Number(o.start)) || Number(o.start) < 0) return `第${i+1}个动态元素起始时间无效`;
        if (!o.end || isNaN(Number(o.end)) || Number(o.end) <= Number(o.start)) return `第${i+1}个动态元素结束时间无效`;
      }
    }
    return "";
  };

  const handleSubmit = async () => {
    setError("");
    setFormError("");
    setVideoUrl("");
    setStatus("提交中...");
    setProgress(0);
    const err = validate();
    if (err) {
      setFormError(err);
      setStatus("");
      return;
    }
    try {
      const id = await submitScript({
        script,
        style,
        voice,
        subtitleFile,
        coverImage,
        overlays: overlays.filter(o => o.image)
      });
      setJobId(id);
      setStatus("处理中...");
      setProgress(5);
      pollStatus(id);
    } catch (e) {
      setError("提交失败：" + e.message);
      setStatus("");
    }
  };

  const pollStatus = (id) => {
    intervalRef.current = setInterval(async () => {
      try {
        const res = await getJobStatus(id);
        setStatus(res.status);
        if (typeof res.progress === "number") {
          setProgress(res.progress);
        } else if (res.status === "processing") {
          setProgress((prev) => (prev < 90 ? prev + 5 : 90));
        }
        if (res.status === "finished") {
          setProgress(100);
          setVideoUrl(res.video_path);
          clearInterval(intervalRef.current);
        }
        if (res.status === "failed") {
          setError("生成失败：" + (res.error || "未知错误"));
          setProgress(0);
          clearInterval(intervalRef.current);
        }
      } catch (e) {
        setError("查询失败：" + e.message);
        setProgress(0);
        clearInterval(intervalRef.current);
      }
    }, 2000);
  };

  // 多overlay操作
  const handleOverlayChange = (idx, key, value) => {
    setOverlays(prev => prev.map((o, i) => i === idx ? { ...o, [key]: value } : o));
  };
  const addOverlay = () => setOverlays([...overlays, { image: null, start: "", end: "", position: "" }]);
  const removeOverlay = idx => setOverlays(overlays.length > 1 ? overlays.filter((_, i) => i !== idx) : overlays);

  return (
    <div style={{
      minHeight: "100vh",
      background: gradientBg,
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      padding: 0,
      flexDirection: "column"
    }}>
      {(styleLoadError || voiceLoadError) && (
        <div style={{ width: "100%", background: errorColor, color: "#fff", textAlign: "center", padding: 10, fontWeight: 700, letterSpacing: 1, fontSize: 16, marginBottom: 8, borderRadius: 0 }}>
          ⚠️ 与后端服务连接异常，部分功能不可用，请检查后端或刷新页面。
        </div>
      )}
      <div style={{
        maxWidth: 520,
        width: "98vw",
        background: cardBg,
        borderRadius: 24,
        boxShadow: "0 8px 32px rgba(127,83,172,0.13)",
        padding: "36px 24px 32px 24px",
        color: accent,
        transition: "box-shadow 0.3s"
      }}>
        <h2 style={{
          textAlign: "center",
          fontWeight: 900,
          letterSpacing: 2,
          color: "transparent",
          background: "linear-gradient(90deg, #7f53ac 0%, #43cea2 100%)",
          WebkitBackgroundClip: "text",
          fontSize: 28,
          marginBottom: 22
        }}>AI 智能体短视频生成</h2>
        <div style={{ marginBottom: 20, background: sectionBg, borderRadius: 12, padding: 16, boxShadow: "0 2px 8px #e0e7ff33" }}>
          <textarea
            value={script}
            onChange={e => setScript(e.target.value)}
            rows={6}
            placeholder="请输入要生成视频的脚本..."
            style={{
              width: "100%",
              minHeight: 120,
              maxHeight: 220,
              borderRadius: 12,
              border: `1.5px solid ${accent}`,
              padding: 14,
              fontSize: 16,
              boxShadow: "0 2px 8px rgba(127,83,172,0.06)",
              outline: "none",
              transition: "border 0.2s, box-shadow 0.2s",
              resize: "vertical",
              lineHeight: 1.7,
              background: "#f7f8fa",
              boxSizing: "border-box"
            }}
            onFocus={e => e.target.style.border = `1.5px solid ${accent2}`}
            onBlur={e => e.target.style.border = `1.5px solid ${accent}`}
          />
        </div>
        <div style={{ display: "flex", gap: 12, marginBottom: 16 }}>
          <select value={style} onChange={e => setStyle(e.target.value)} style={{ flex: 1, borderRadius: 8, padding: 10, fontSize: 15, border: `1.2px solid ${accent}` }} disabled={styleLoadError}>
            <option value="">{styleLoadError ? "图片风格加载失败" : "选择图片风格"}</option>
            {styles.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
          <select value={voice} onChange={e => setVoice(e.target.value)} style={{ flex: 1, borderRadius: 8, padding: 10, fontSize: 15, border: `1.2px solid ${accent}` }} disabled={voiceLoadError}>
            <option value="">{voiceLoadError ? "TTS声音加载失败" : "选择TTS声音"}</option>
            {voices.map(v => <option key={v} value={v}>{v}</option>)}
          </select>
        </div>
        <div style={{ display: "flex", gap: 12, marginBottom: 16 }}>
          <label style={{ flex: 1 }}>
            <span style={{ fontSize: 14, color: accent }}>字幕文件</span>
            <input type="file" accept=".srt,.ass,.vtt" onChange={e => setSubtitleFile(e.target.files[0])} style={{ width: "100%", marginTop: 4 }} />
          </label>
          <label style={{ flex: 1 }}>
            <span style={{ fontSize: 14, color: accent }}>封面图片</span>
            <input type="file" accept="image/*" onChange={e => setCoverImage(e.target.files[0])} style={{ width: "100%", marginTop: 4 }} />
          </label>
        </div>
        <div style={{ background: sectionBg, borderRadius: 12, padding: 14, marginBottom: 18, boxShadow: "0 1px 4px #e0e7ff22" }}>
          <div style={{ fontWeight: 700, marginBottom: 8, color: accent2, fontSize: 16, letterSpacing: 1 }}>动态元素（Overlay，可添加多个）</div>
          {overlays.map((o, idx) => (
            <div key={idx} style={{ display: "flex", alignItems: "center", gap: 8, marginBottom: 8, background: "#fff", borderRadius: 8, padding: 8, boxShadow: "0 1px 4px #e0e7ff22" }}>
              <input type="file" accept="image/*" onChange={e => handleOverlayChange(idx, "image", e.target.files[0])} style={{ flex: 2 }} />
              <input type="number" min="0" step="0.1" placeholder="起始时间(s)" value={o.start} onChange={e => handleOverlayChange(idx, "start", e.target.value)} style={{ flex: 1, borderRadius: 6, padding: 6, border: `1px solid ${accent}` }} />
              <input type="number" min="0" step="0.1" placeholder="结束时间(s)" value={o.end} onChange={e => handleOverlayChange(idx, "end", e.target.value)} style={{ flex: 1, borderRadius: 6, padding: 6, border: `1px solid ${accent}` }} />
              <input type="text" placeholder="位置(如10:10)" value={o.position} onChange={e => handleOverlayChange(idx, "position", e.target.value)} style={{ flex: 1, borderRadius: 6, padding: 6, border: `1px solid ${accent}` }} />
              <button onClick={() => removeOverlay(idx)} style={{ background: "#fff0f3", color: errorColor, border: `1px solid ${errorColor}33`, borderRadius: 6, padding: "4px 10px", fontWeight: 700, cursor: overlays.length > 1 ? "pointer" : "not-allowed", fontSize: 16, marginLeft: 2, transition: "background 0.2s" }} disabled={overlays.length === 1}>✕</button>
            </div>
          ))}
          <button onClick={addOverlay} style={{ background: "linear-gradient(90deg, #a8edea 0%, #fed6e3 100%)", color: accent, border: `1.2px solid ${accent2}"`, borderRadius: 8, padding: "6px 18px", fontWeight: 700, fontSize: 15, marginTop: 4, cursor: "pointer", boxShadow: "0 1px 4px #e0e7ff22", transition: "background 0.2s" }}>+ 添加动态元素</button>
        </div>
        {formError && <div style={{ color: errorColor, fontWeight: 700, marginBottom: 10, textAlign: "center" }}>{formError}</div>}
        <div style={{ display: "flex", justifyContent: "center", marginBottom: 10 }}>
          <button
            onClick={handleSubmit}
            disabled={status === "处理中..."}
            style={{
              background: "linear-gradient(90deg, #43cea2 0%, #7f53ac 100%)",
              color: "#fff",
              border: "none",
              borderRadius: 12,
              padding: "14px 44px",
              fontSize: 20,
              fontWeight: 800,
              cursor: status === "处理中..." ? "not-allowed" : "pointer",
              boxShadow: "0 2px 12px rgba(127,83,172,0.10)",
              marginBottom: 0,
              transition: "background 0.3s, box-shadow 0.3s, transform 0.1s",
              outline: "none"
            }}
            onMouseDown={e => e.currentTarget.style.transform = "scale(0.97)"}
            onMouseUp={e => e.currentTarget.style.transform = "scale(1)"}
            onMouseLeave={e => e.currentTarget.style.transform = "scale(1)"}
          >
            一键生成视频
          </button>
        </div>
        <div style={{ margin: "10px 0", minHeight: 24, textAlign: "center" }}>
          {status && <span>任务状态：{status}</span>}
          {error && <div style={{ color: errorColor, fontWeight: 600 }}>{error}</div>}
        </div>
        {status === "processing" && (
          <div style={{ margin: "18px 0" }}>
            <div style={{
              background: sectionBg,
              borderRadius: 12,
              height: 22,
              width: "100%",
              overflow: "hidden",
              marginBottom: 6,
              boxShadow: "0 1px 6px #a8edea33"
            }}>
              <div style={{
                width: `${progress}%`,
                height: "100%",
                background: "linear-gradient(90deg, #43cea2 0%, #7f53ac 100%)",
                transition: "width 0.7s cubic-bezier(.4,2,.6,1)",
                boxShadow: "0 0 12px #43cea2aa"
              }} />
            </div>
            <div style={{
              textAlign: "right",
              fontWeight: 700,
              color: accent,
              fontVariantNumeric: "tabular-nums",
              fontSize: 16,
              letterSpacing: 1
            }}>{progress}%</div>
          </div>
        )}
        {videoUrl && (
          <div style={{ marginTop: 28, textAlign: "center" }}>
            <video src={videoUrl} controls width="100%" style={{
              borderRadius: 18,
              boxShadow: "0 2px 16px rgba(127,83,172,0.18)",
              marginBottom: 12,
              maxHeight: 340,
              background: "#000"
            }} />
            <br />
            <a href={videoUrl} download style={{
              display: "inline-block",
              marginTop: 12,
              background: "linear-gradient(90deg, #43cea2 0%, #7f53ac 100%)",
              color: "#fff",
              padding: "12px 36px",
              borderRadius: 12,
              fontWeight: 800,
              textDecoration: "none",
              fontSize: 18,
              boxShadow: "0 2px 8px #7f53ac22",
              transition: "background 0.3s"
            }}>下载视频</a>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
