import React, { useState, useRef, useEffect } from "react";
import { submitScript, getJobStatus, getImageStyles, getTTSVoices } from "./api";

function App() {
  const [script, setScript] = useState("");
  const [jobId, setJobId] = useState("");
  const [status, setStatus] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  const [error, setError] = useState("");
  const [progress, setProgress] = useState(0);
  const [style, setStyle] = useState("");
  const [voice, setVoice] = useState("");
  const [styles, setStyles] = useState([]);
  const [voices, setVoices] = useState([]);
  const [subtitleFile, setSubtitleFile] = useState(null);
  const [coverImage, setCoverImage] = useState(null);
  const [overlayImage, setOverlayImage] = useState(null);
  const [overlayStart, setOverlayStart] = useState("");
  const [overlayEnd, setOverlayEnd] = useState("");
  const [overlayPos, setOverlayPos] = useState("");
  const intervalRef = useRef(null);

  useEffect(() => {
    getImageStyles().then(setStyles);
    getTTSVoices().then(setVoices);
  }, []);

  const handleSubmit = async () => {
    setError("");
    setVideoUrl("");
    setStatus("提交中...");
    setProgress(0);
    try {
      const id = await submitScript({
        script,
        style,
        voice,
        subtitleFile,
        coverImage,
        overlay: overlayImage ? {
          image: overlayImage,
          start: overlayStart,
          end: overlayEnd,
          position: overlayPos
        } : null
      });
      setJobId(id);
      setStatus("处理中...");
      setProgress(5);
      pollStatus(id);
    } catch (e) {
      setError("提交失败：" + e.message);
      setStatus("");
    }
  };

  const pollStatus = (id) => {
    intervalRef.current = setInterval(async () => {
      try {
        const res = await getJobStatus(id);
        setStatus(res.status);
        if (typeof res.progress === "number") {
          setProgress(res.progress);
        } else if (res.status === "processing") {
          setProgress((prev) => (prev < 90 ? prev + 5 : 90));
        }
        if (res.status === "finished") {
          setProgress(100);
          setVideoUrl(res.video_path);
          clearInterval(intervalRef.current);
        }
        if (res.status === "failed") {
          setError("生成失败：" + (res.error || "未知错误"));
          setProgress(0);
          clearInterval(intervalRef.current);
        }
      } catch (e) {
        setError("查询失败：" + e.message);
        setProgress(0);
        clearInterval(intervalRef.current);
      }
    }, 2000);
  };

  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      padding: 0,
    }}>
      <div style={{
        maxWidth: 480,
        width: "98vw",
        background: "rgba(255,255,255,0.97)",
        borderRadius: 20,
        boxShadow: "0 8px 32px rgba(60,60,120,0.18)",
        padding: "32px 20px 28px 20px",
        color: "#222",
        transition: "box-shadow 0.3s"
      }}>
        <h2 style={{
          textAlign: "center",
          fontWeight: 800,
          letterSpacing: 2,
          color: "transparent",
          background: "linear-gradient(90deg, #667eea 0%, #fc5c7d 100%)",
          WebkitBackgroundClip: "text",
          fontSize: 26,
          marginBottom: 18
        }}>AI 智能体短视频生成</h2>
        <div style={{ marginBottom: 18 }}>
          <textarea
            value={script}
            onChange={e => setScript(e.target.value)}
            rows={6}
            placeholder="请输入要生成视频的脚本..."
            style={{
              width: "100%",
              minHeight: 120,
              maxHeight: 220,
              borderRadius: 12,
              border: "1.5px solid #d1d5db",
              padding: 14,
              fontSize: 16,
              boxShadow: "0 2px 8px rgba(0,0,0,0.06)",
              outline: "none",
              transition: "border 0.2s, box-shadow 0.2s",
              resize: "vertical",
              lineHeight: 1.7,
              background: "#f7f8fa",
              boxSizing: "border-box"
            }}
            onFocus={e => e.target.style.border = "1.5px solid #667eea"}
            onBlur={e => e.target.style.border = "1.5px solid #d1d5db"}
          />
        </div>
        <div style={{ display: "flex", gap: 10, marginBottom: 14 }}>
          <select value={style} onChange={e => setStyle(e.target.value)} style={{ flex: 1, borderRadius: 8, padding: 8, fontSize: 15 }}>
            <option value="">选择图片风格</option>
            {styles.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
          <select value={voice} onChange={e => setVoice(e.target.value)} style={{ flex: 1, borderRadius: 8, padding: 8, fontSize: 15 }}>
            <option value="">选择TTS声音</option>
            {voices.map(v => <option key={v} value={v}>{v}</option>)}
          </select>
        </div>
        <div style={{ display: "flex", gap: 10, marginBottom: 14 }}>
          <label style={{ flex: 1 }}>
            <span style={{ fontSize: 14 }}>字幕文件</span>
            <input type="file" accept=".srt,.ass,.vtt" onChange={e => setSubtitleFile(e.target.files[0])} style={{ width: "100%" }} />
          </label>
          <label style={{ flex: 1 }}>
            <span style={{ fontSize: 14 }}>封面图片</span>
            <input type="file" accept="image/*" onChange={e => setCoverImage(e.target.files[0])} style={{ width: "100%" }} />
          </label>
        </div>
        <div style={{ border: "1px solid #e0e7ff", borderRadius: 10, padding: 12, marginBottom: 14, background: "#f7f8fa" }}>
          <div style={{ fontWeight: 600, marginBottom: 6, color: "#667eea" }}>动态元素（Overlay，可选）</div>
          <div style={{ display: "flex", gap: 8, marginBottom: 6 }}>
            <input type="file" accept="image/*" onChange={e => setOverlayImage(e.target.files[0])} style={{ flex: 2 }} />
            <input type="number" min="0" step="0.1" placeholder="起始时间(s)" value={overlayStart} onChange={e => setOverlayStart(e.target.value)} style={{ flex: 1, borderRadius: 6, padding: 4 }} />
            <input type="number" min="0" step="0.1" placeholder="结束时间(s)" value={overlayEnd} onChange={e => setOverlayEnd(e.target.value)} style={{ flex: 1, borderRadius: 6, padding: 4 }} />
            <input type="text" placeholder="位置(如10:10)" value={overlayPos} onChange={e => setOverlayPos(e.target.value)} style={{ flex: 1, borderRadius: 6, padding: 4 }} />
          </div>
        </div>
        <div style={{ display: "flex", justifyContent: "center", marginBottom: 10 }}>
          <button
            onClick={handleSubmit}
            disabled={!script || !style || !voice || status === "处理中..."}
            style={{
              background: "linear-gradient(90deg, #43cea2 0%, #185a9d 100%)",
              color: "#fff",
              border: "none",
              borderRadius: 10,
              padding: "12px 36px",
              fontSize: 18,
              fontWeight: 700,
              cursor: !script || !style || !voice || status === "处理中..." ? "not-allowed" : "pointer",
              boxShadow: "0 2px 12px rgba(0,0,0,0.10)",
              marginBottom: 0,
              transition: "background 0.3s, box-shadow 0.3s, transform 0.1s",
              outline: "none"
            }}
            onMouseDown={e => e.currentTarget.style.transform = "scale(0.97)"}
            onMouseUp={e => e.currentTarget.style.transform = "scale(1)"}
            onMouseLeave={e => e.currentTarget.style.transform = "scale(1)"}
          >
            一键生成视频
          </button>
        </div>
        <div style={{ margin: "10px 0", minHeight: 24, textAlign: "center" }}>
          {status && <span>任务状态：{status}</span>}
          {error && <div style={{ color: "#e53e3e", fontWeight: 600 }}>{error}</div>}
        </div>
        {status === "processing" && (
          <div style={{ margin: "18px 0" }}>
            <div style={{
              background: "#e0e7ff",
              borderRadius: 12,
              height: 22,
              width: "100%",
              overflow: "hidden",
              marginBottom: 6,
              boxShadow: "0 1px 6px rgba(102,126,234,0.12)"
            }}>
              <div style={{
                width: `${progress}%`,
                height: "100%",
                background: "linear-gradient(90deg, #43cea2 0%, #185a9d 100%)",
                transition: "width 0.7s cubic-bezier(.4,2,.6,1)",
                boxShadow: "0 0 12px #43cea2aa"
              }} />
            </div>
            <div style={{
              textAlign: "right",
              fontWeight: 700,
              color: "#185a9d",
              fontVariantNumeric: "tabular-nums",
              fontSize: 16,
              letterSpacing: 1
            }}>{progress}%</div>
          </div>
        )}
        {videoUrl && (
          <div style={{ marginTop: 24, textAlign: "center" }}>
            <video src={videoUrl} controls width="100%" style={{
              borderRadius: 16,
              boxShadow: "0 2px 16px rgba(0,0,0,0.18)",
              marginBottom: 10,
              maxHeight: 320,
              background: "#000"
            }} />
            <br />
            <a href={videoUrl} download style={{
              display: "inline-block",
              marginTop: 10,
              background: "linear-gradient(90deg, #43cea2 0%, #185a9d 100%)",
              color: "#fff",
              padding: "10px 32px",
              borderRadius: 10,
              fontWeight: 700,
              textDecoration: "none",
              boxShadow: "0 2px 8px rgba(0,0,0,0.10)",
              transition: "background 0.3s"
            }}>下载视频</a>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
